}
.nav ul li a:before {
  position: absolute;
  content: "";
  top: 0;
  left: 0;
  width: 0;
  height: 100%;
  transition: width 250ms ease;
  z-index: -1;
}
.nav ul li a:hover {
  color: #004544;
}
.nav ul li a:hover:before {
  width: 100%;
  height: 100%;
  background-color: #23a696;
  transition: width 250ms ease;
}

